mapper.select:
select recordinfo.*,recordclass.recordClass
from recordinfo  join recordclass
on recordinfo.recordClassId=recordclass.id
where recordinfo.appId=#{arg0}
order by id limit #{arg1},#{arg2}

select recordinfo.*,recordclass.recordClass
from recordinfo  join recordclass
on recordinfo.recordClassId=recordclass.id
where recordclass.id=#{arg0} and recordinfo.appId=#{arg1}
order by id limit #{arg2},#{arg3}

select recordinfo.* from recordinfo
 where recordClassId=-1 and appId=#{arg0}
 order by rand() limit #{arg1}
